import React from "react";
import { GetServerSidePropsContext } from "next";
import fs from "fs";
import dynamic from "next/dynamic";

function Book({ SpecificBook }) {
  return <SpecificBook />;
}

export async function getServerSideProps(context: GetServerSidePropsContext) {
  const files = fs.readdirSync("./books");
  const fileName = files.filter(
    (fileName) =>
      fileName.slice(0, 11).slice(0, -4) === (context.query.title as string)
  )[0];
  const SpecificBook = dynamic(() => import(fileName));
  return {
    props: {
      SpecificBook,
    },
  };
}

export default Book;
