import Title from "../components/Title";
import PageBorder from "../components/PageBorder";
import fs from "fs";

function Books({ books }) {
  return (
    <PageBorder>
      <Title>Books</Title>
      {books?.length ? (
        <ul className="pl-6 list-disc">
          {books.map(({ title, timestamp }) => (
            <li className="tracking-wider text-gray-700 text-lighter">
              {title} {timestamp}
            </li>
          ))}
        </ul>
      ) : (
        <p className="tracking-wider text-gray-700 text-lighter">
          Nothing here yet. Check back soon, I guess.
        </p>
      )}
    </PageBorder>
  );
}

export async function getServerSideProps(context) {
  const files = fs.readdirSync("./books");
  const books = files.map((fileName) => {
    return {
      timestamp: fileName.slice(0, 10),
      title: fileName.slice(10).slice(0, -4),
    };
  });
  return {
    props: {
      books,
    },
  };
}
export default Books;
