import React from "react";
import Link from "next/link";

function Header() {
  return (
    <>
      <div className="fixed top-0 left-0 right-0 h-12 overflow-x-auto bg-white">
        <div className="absolute top-0 left-0 flex items-center h-12 px-4 space-x-4 text-lg font-light text-gray-700 whitespace-no-wrap right-fade">
          <Link href="/">
            <a className="hover:underline">Home</a>
          </Link>
          <Link href="/books">
            <a className="hover:underline">Books</a>
          </Link>
          <Link href="/programming">
            <a className="hover:underline">Programming</a>
          </Link>
          <Link href="philosophy">
            <a className="hover:underline">Politics & Philosophy</a>
          </Link>
        </div>
      </div>
      <style jsx>{`
        .right-fade {
          background: red;
        }
        .right-fade::after {
          content: "";
          position: absolute;
          top: 0;
          right: 0;
          bottom: 0;
          width: 16px;
          background: blue;
        }
      `}</style>
    </>
  );
}

export default Header;
